# Binary Search using recursion
def search(arr, target, s, e):
    if s > e:
        return -1

    middle = s + (e - s) // 2

    if target == arr[middle]:
        return middle

    if target < arr[middle]:
        return search(arr, target, s, middle - 1)

    return search(arr, target, middle + 1, e)


