class Solution:
    def __init__(self):
        self.__values = {'M':1000,
                         'D':500,
                         'C':100,
                         'L':50,
                         'X':10,
                         'V':5,
                         'I':1
        }

    def romanToInt(self, s: str) -> int:
        if s == "":
            return 0
        elif len(s) == 1:
            if s in self.__values:
                return self.__values[s]
        elif len(s) == 2:
            left = self.romanToInt(s[0])
            right = self.romanToInt(s[1])
            if left < right:
                return right - left
            return left + right
        else:
            first = self.romanToInt(s[0])
            second = self.romanToInt(s[1])
            if first < second:
                return -first + self.romanToInt(s[1:])
            return first + self.romanToInt(s[1:])

a = Solution()
print(f"MCMXCIV = {a.romanToInt('MCMXCIV')}")

